import React, { Component } from "react";
// This will require to npm install axios
import axios from 'axios';
import {
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button, Modal, ModalHeader
} from 'reactstrap';
import { Link } from "react-router-dom";

const Record = (props) => (
  <tr>
    <td>{props.record.courseTitle}</td>
    <td>{props.record.termDesc}</td>
    <td>{props.record.courseNumber}</td>
    <td>{props.record.subjectDescription}</td>

    <td>
      <Link to={"/edit/" + props.record._id}>Edit</Link> |
      <a
        href="/"
        onClick={() => {
          props.deleteRecord(props.record._id);
        }}
      >
        Delete
      </a>
    </td>
  </tr>
);

export default class RecordList extends Component {
  // This is the constructor that shall store our data retrieved from the database
  constructor(props) {
    super(props);
    this.deleteRecord = this.deleteRecord.bind(this);
    this.state = { records: [] };
  }

  // This method will get the data from the database.
  componentDidMount() {
    axios
      .get("http://localhost:5000/record/")
      .then((response) => {
        this.setState({ records: response.data });
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  // This method will delete a record based on the method
  deleteRecord(id) {
    axios.delete("http://localhost:5000/" + id).then((response) => {
      console.log(response.data);
    });

    this.setState({
      record: this.state.records.filter((el) => el._id !== id),
    });
  }

  // This method will map out the users on the table
  recordList() {
    return this.state.records.map((currentrecord) => {
      return (
        <Record
          record={currentrecord}
          deleteRecord={this.deleteRecord}
          key={currentrecord._id}
        />
      );
    });
  }

  //


  // This following section will display the table with the records of individuals.
  render() {
    const [checkedOne, setCheckedOne] = React.useState(false);
          const [checkedTwo, setCheckedTwo] = React.useState(false);

          const handleChangeOne = () => {
            setCheckedOne(!checkedOne);
          };

          const handleChangeTwo = () => {
          setCheckedTwo(!checkedTwo);
          };
    return (
      <div>
        <div class="row">
          <div class="col-md-3">
          <input type="search" class="form-control rounded" placeholder="Search ..." aria-label="Search"></input>
          </div>
          <div class="col-md-9">
          <h3>Course Catalog</h3>
            <div>
            <Checkbox
              label="Value 1"
              value={checkedOne}
              onChange={handleChangeOne}
            />
            <Checkbox
              label="Value 2"
              value={checkedTwo}
              onChange={handleChangeTwo}
    />
    </div>

        <table className="table table-striped" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Course Name</th>
              <th>Term</th>
              <th>Course Number</th>
              <th>Department</th>
            </tr>
          </thead>
          <tbody>{this.recordList()}</tbody>
        </table>
          </div>
        </div>
      </div>
    );
  }
}
